CREATE MATERIALIZED VIEW country_names_smooth AS
SELECT
  ogc_fid,
  ST_ChaikinSmoothing(geometry, 3)::geometry(LineString, 3857) AS geometry,
  name,
  "name:en"
FROM country_names;

CREATE UNIQUE INDEX country_names_smooth_pk
ON country_names_smooth (ogc_fid);

CREATE INDEX country_names_smooth_geometry_idx
ON country_names_smooth
USING GIST (geometry);
