SET standard_conforming_strings = ON;
DROP TABLE IF EXISTS "public"."geonames" CASCADE;
BEGIN;
CREATE TABLE "public"."geonames"();
ALTER TABLE "public"."geonames" ADD COLUMN "ogc_fid" SERIAL CONSTRAINT "geonames_pk" PRIMARY KEY;
SELECT AddGeometryColumn('public','geonames','geometry',3857,'LINESTRING',2);
ALTER TABLE "public"."geonames" ADD COLUMN "name" VARCHAR(254);
CREATE INDEX "geonames_wkb_geometry_geom_idx" ON "public"."geonames" USING GIST ("geometry");
COMMIT;

CREATE MATERIALIZED VIEW geonames_smooth AS
SELECT
  ogc_fid,
  ST_ChaikinSmoothing(geometry, 3)::geometry(LineString, 3857) AS geometry,
  name
FROM geonames;

CREATE UNIQUE INDEX geonames_smooth_pk
ON geonames_smooth (ogc_fid);

CREATE INDEX geonames_smooth_geometry_idx
ON geonames_smooth
USING GIST (geometry);
